sys_script_include.script:
var Ticket = Class.create();
Ticket.prototype = {
	initialize: function() {
	},

	create: function(request) {
		gs.info('WA | It Worked!');
		gs.info('WA | Parsed ' + JSON.stringify(request));

		var gr = new GlideRecord('incident');
		gr.initialize();
		gr.short_description = "Watson Ticket";
		var inc = gr.insert();

		var lookup = new GlideRecord('incident');
		lookup.get(inc);

		var answer = {};
		answer.http_status = "200";
		answer.status_message = "Payload accepted and processed. Created " + lookup.number;

		gs.debug('WA| ' + JSON.stringify(answer));
		return answer;
	},

	type: 'Ticket'
};
